section.script:
         //funktioniert wegen Cross-Origin-Requests nur auf dem eigenen Server
        $.getJSON('../../api/v1/search?q=objectType:mods&rows=5&sort=modified desc&wt=json',
            function (data) {
              $('#doc_count').text(data.response.numFound);

              $(data.response.docs).each(function(i, doc){
                $('#last5').append($('
')
                    .append($('').append($('
')
                      .append($('').append(
                        '
'+doc['search_result_link_text']+'',
                          '
'+doc['mods.title.main']:''+'',
                          '
'+doc['modifiedby']+', am ' +new Date(doc['modified']).toLocaleString()+''
                      )))));
            });
        });
